var scroll=window.requestAnimationFrame||function(e){window.setTimeout(e,100/60)},fadeInElements=document.querySelectorAll(".fade");function loopFade(){fadeInElements.forEach(function(e){isElementInViewPort(e)?e.classList.add("fadeIn"):e.classList.remove("fadeIn")}),scroll(loopFade)}function isElementInViewPort(e){var t=e.getBoundingClientRect();return t.top<=0&&0<=t.bottom||t.bottom>=(window.innerHeight||document.documentElement.clientHeight)&&t.top<=(window.innerHeight||document.documentElement.clientHeight)||0<=t.top&&t.bottom<=(window.innerHeight||document.documentElement.clientHeight)}loopFade();